fileroot.script:
switch = "on";
vent = "closed";
shkaf = "locked";
switch = "on";
toolboxfaze = "one";
box = "faze1";
inHand = "None";

Mask.script:
if inHand == "otvertka" then
  SendMessage("messageWindow" , "тыкать коробки отверткой - совершенно бесполезное занятие" ) ;
else
  SendMessage("messageWindow" , "пустые картонные коробки из под провианта" ) ;
end

				if switch == "on" then
					switch = "off";
          SendMessage("video", "play");
				else
					switch = "on";
          SendMessage("video", "stop");
				end

if (inHand == "otvertka") then
  SendMessage("messageWindow" , "огнетушитель отверткой не проткнешь" ) ;
else
  SendMessage("messageWindow" , "огнетушитель,... мне? нет не нужен" ) ;
end

if (inHand ~= "None") then
  SendMessage("messageWindow" , "даже в минуту глубоко безделья я туда не полезу ради такой ерунды" ) ;
else
  SendMessage("messageWindow" , "надпись: 'в случае аварии разбить стекло' - бородатая космическая шутка" ) ;
end

      if (inHand ~= "None") then
        SendMessage("messageWindow" , "нет,... не буду выполнять столь странное задание" ) ;
      else
				SendMessage("messageWindow" , "надпись:   'ОСТОРОЖНО: ОТКРЫТЫЙ КОСМОС!'" ) ;
      end

      if (inHand ~= "None") then
        SendMessage("messageWindow" , "нет, это совершенно бесполезно" ) ;
      else
				SendMessage("messageWindow" , "это механизм для аварийного открывания двери,... но он давно заржавел" ) ;
      end

        if (inHand ~= "None") then
          SendMessage("messageWindow" , "нет, этим стекло не сломать - тут танк нужен" ); 
        else
				  SendMessage("messageWindow" , "космос, глубокий и безмятежный - постойка,.. кажется, корабль абсолютно неподвижен - это странно" );
        end

if (inHand ~= "None") then
  SendMessage("messageWindow" , "глупо,.. ну ведь это же глупо!!!" ) ;
else
  SendMessage("messageWindow" , "трубы какие-то,... сейчас мне от них пользы ноль, но в дороге они меня грели" ) ;
end

if (inHand == "otvertka") then
	SendMessage("messageWindow" , "не, отверкой эту дверь не сломать..." ) ;
else
	SendMessage("messageWindow" , "через дверь мне из этого склада без личного кода не выйти - это, так сказать, минусы путешествия зайцем.." ) ;
end

MultiTexture.script:
if (box == "faze1") then
  box = "faze2";
else
  if (inHand == "otvertka") then
    SendMessage("messageWindow" , "тыкать коробки отверткой - совершенно бесполезное занятие" ) ;
  else
    SendMessage("messageWindow" , "пустые картонные коробки из под провианта" ) ;
  end
end

if (shkaf == "closed") then
  shkaf = "opened";
elseif (shkaf == "opened") then
  shkaf = "closed";
elseif (inHand == "key") then
   shkaf = "opened";
   SendMessage("inventory" , "remove from hand" );
else
  SendMessage("messageWindow" , "ключик нада, где тут валялся - простенький такой" ) ;
end
